/*
 * @Descripttion: 
 * @Author: guoxiaoqiang
 * @Date: 2022-07-19 10:08:08
 * @LastEditors: Please set LastEditors
 * @LastEditTime: 2022-07-21 09:39:59
 * @FilePath: index.jsx
 */
import React, { Component } from 'react'

export default class Item extends Component {
  constructor(props){
    super(props)
    this.state={mouse:false}
  }
  handleMouse=(parameter)=>{
     return ()=>{
      this.setState({
        mouse:parameter
      })
     }
  }
  render() {
    const {name,done} = this.props
    const {mouse}=this.state
    return (
      <li   style={{backgroundColor:mouse?'#ccc':'#ffffff'}} onMouseEnter={this.handleMouse(true)} onMouseLeave={this.handleMouse(false)}>
        <input type="checkbox" defaultChecked={done}/>
        <span>{name}</span>
        <button style={{display:mouse?'block':'none'}} on>删除</button>
      </li>
    )
  }
}
