import React, { Component } from 'react'
import { Select } from 'antd';
const { Option } = Select;
export default class Count extends Component {
    constructor(props) {
        super(props)
        this.state = {
            count: 1
        }
    }
    handleChange = (value) => {
        this.setState({
            count: value
        })
    };


    // handleChange = (value) => {
    //     console.log(value)
    //     const {count}=this.state
    //     this.setState({
    //         count:count+value
    //     })
    // }
    increment = () => {
        const { value } = this.www
        const { count } = this.state
        this.setState({
            count: count + value * 1
        })
    }
    add = () => {
        const { value } = this.www
        const { count } = this.state
        this.setState({
            count: count + value * 1
        })
    }
    render() {
        return (
            <div>
                <h1>汉子:{this.state.count}</h1>
                <Select
                    defaultValue="lucy"
                    style={{
                        width: 120,
                    }}
                    onChange={this.handleChange}
                >
                    <Option value="1">Jack</Option>
                    <Option value="2">Lucy</Option>
                    <Option value="3" >
                        3
                    </Option>
                    <Option value="4">yiminghe</Option>
                </Select>
                <Select
                    defaultValue="lucy"
                    style={{
                        width: 120,
                    }}
                    disabled
                >
                    <Option value="lucy">Lucy</Option>
                </Select>











                <select ref={c => { this.www = c }}>
                    <option value="1">1</option>
                    <option value="2">2</option>
                    <option value="3">3</option>
                    <option value="4">4</option>
                </select>
                <button onClick={this.add}></button>
                <button onClick={this.increment}>+</button>&nbsp;&nbsp;&nbsp;
                <button onClick={this.decrement}>-</button>&nbsp;&nbsp;
                <button onClick={this.incrementIfOdd}>和为奇数再加</button>&nbsp;&nbsp;
                <button onClick={this.incrementAsync}>异步在加</button>&nbsp;&nbsp;&nbsp;
            </div>
        )
    }
}
